import Piece from '../Piece.js';

export default class Pawn extends Piece {
  constructor(props) {
    super(props, (props === 1 ?
      "https://upload.wikimedia.org/wikipedia/commons/4/45/Chess_plt45.svg" :
      "https://upload.wikimedia.org/wikipedia/commons/c/c7/Chess_pdt45.svg"));
    this.state = {
      firstMove: true,
      captured: false,
      value: 1,
    };
    console.log(this.state);
    console.log(props);
  }

  isValidMove = (origin, destination) => {
    const { firstMove } = this.state;
    const proposedMove = Math.abs(origin - destination);
    let incrementor = 0;
    if(proposedMove % 9 === 0) {
      incrementor = 9;
      return incrementor;
    } else if (proposedMove % 8 === 0) {
      incrementor = 8;
      return incrementor;
    } else if (proposedMove % 7 === 0) {
      incrementor = 7;
      return incrementor;
    } else if (proposedMove % 16 === 0 && firstMove === true) {
      incrementor = 16;
      return incrementor;
    } else {
      alert("The" + `${this.name}` + "'s movements have been restricted.");
      return incrementor = 400;
    }
    return incrementor;
  }

  promotion = () => {

  }
}
