  generateMovePath = (origin, destination, incrementor) => {
    const path = [];
    if( origin < destination ) {
      for(let index = origin + incrementor; index < destination + 1; index += incrementor) {
        path.push(index);
      }
    } else {
      for(let index = destination; index < origin + 1; index += incrementor) {
        path.push(index);
      }
    }
    return path;
  }

