import React from 'react';

export default class Pieces extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      captured: false,
    };
  }

  generateMovePath = (origin, destination, incrementor) => {
    const path = [];
    if( origin < destination ) {
      for(let index = origin + incrementor; index < destination + 1; index += incrementor) {
        path.push(index);
      }
    } else {
      for(let index = destination + incrementor; index < origin + 1; index += incrementor) {
        path.push(index);
      }
    }
    return path;
  }

