import React from 'react';
import Pieces from '../Pieces.js'

export default class Queen extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      captured: false,
      image: props.player === 1 ? '' : ''
    };
  }

  isValidMove = (source, destination) => {
    const proposedMove = Math.abs(source - destination);
    let incrementor = 0;
    switch (proposedMove) {
      case proposedMove % 9 === 0:
        return incrementor = 9;
        break;
      case proposedMove % 8 === 0:
        return incrementor = 8;
        break;
      case proposedMove % 7 === 0:
        return incrementor = 7;
        break;
      case proposedMove % 1 === 0 && proposedMove < 8:
        return incrementor = 1;
        break;
      default:
        alert("The" + `${this.name}` + "'s movements have been restricted.");
        return incrementor = 400;
    }
  }

  generatePath = (source, destination, incrementor) => {
    const path = [];
    if( source < destination ) {
      for(let index = source + incrementor; index < destination + 1; index += incrementor) {
        path.push(index);
      }
    } else {
      for(let index = destination + incrementor; index < source + 1; index += incrementor) {
        path.push(index);
      }
    }
    return path;
  }

  render() {
    return(
      <div>
      </div>
    )
  }
}
