import React from 'react';
import './Piece.css';

export default class Piece extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      player: props.value,
      style: {
        backgroundImage: ''
      },
    }
    console.log(this.state);
  }

  generateMovePath = (origin, destination, incrementor) => {
    const path = [];
    if( origin < destination ) {
      for(let index = origin + incrementor; index < destination + 1; index += incrementor) {
        path.push(index);
      }
    } else {
      for(let index = destination; index < origin + 1; index += incrementor) {
        path.push(index);
      }
    }
    return path;
  }

  render() {
    return(
      <div>
      </div>
    )
  }
}
