import React from 'react';
import ReactDOM from 'react-dom';
import Board from '../Board/Board.js';
import Piece from './Pieces/Pieces.js';
import Bishop from './Pieces/Bishop/Bishop.js';
import King from './Pieces/King/King.js';
import Knight from './Pieces/Knight/Knight.js';
import Pawn from './Pieces/Pawn/Pawn.js';
import Queen from './Pieces/Queen/Queen.js';
import Rook from './Pieces/Rook/Rook.js';
import initializeBoard from './Helpers/InitializeBoard.js';

export default class Game extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      squares: initializeBoard(),
      users: [],
      whiteCaptured: [],
      blackCaptured: [],
      whiteScore: 0,
      blackScore: 0,
      player: 1,
      turn: 'white',
      selectedPiece: -1,
      status: '',
    }
  }

  render() {
    const { squares } = this.state;
    return(
      <div>
        <Board squares={squares} selectSquare={this.selectSquare}/>
      </div>
    )
  }
}
