import React from 'react';
import Piece from '../Piece.js';

export default class Bishop extends Piece {
  constructor(props) {
    console.log(props);
    super(props.player, (props.player === 1 ?
      "https://upload.wikimedia.org/wikipedia/commons/b/b1/Chess_blt45.svg" :
      "https://upload.wikimedia.org/wikipedia/commons/9/98/Chess_bdt45.svg"));
    this.state = {
      captured: false,
      value: 3,
    };
  }


  isValidMove = (origin, destination) => {
    const proposedMove = Math.abs(origin - destination);
    let incrementor = 0;
    switch (proposedMove) {
      case proposedMove % 9 === 0:
        return incrementor = 9;
        break;
      case proposedMove % 7 === 0:
        return incrementor = 7;
        break;
      default:
        alert("The" + `${this.name}` + "'s movements have been restricted.");
        return incrementor = 400;
    }
  }

  render() {
    return(
      <div>
      </div>
    )
  }
}
