import React from 'react';
import ReactDOM from 'react-dom';
import Square from './Square/Square.js';
import './Board.css';

export default class Board extends React.Component {

  renderSquare = (index, squareShade) => {
    const { squares, selectSquare } = this.props;
    return(
      <Square
        key={squares[index] + Date.now() + Math.random()}
        style={squares[index] ? squares[index].style : null}
        shade={squareShade}
        onClick={selectSquare(index)}
      />
    )
  }

  buildBoard = () => {
    const board = [];
    for(let rankNumber = 0; rankNumber < 8; rankNumber++) {
      const ranks = [];
      for(let file = 0; file < 8; file++) {
        const squareShade = (isEven(rankNumber) && isEven(file)) || (!isEven(rankNumber) && !isEven(file)) ? "light-square" : "dark-square";
        ranks.push(this.renderSquare((rankNumber * 8) + file, squareShade));
      }
      board.push(<div className="Board-rank" key={Date.now() + Math.random()}>{ranks}</div>);
    }
    return board;
  }

  render() {
    return(
      <div>
        {this.buildBoard()}
      </div>
    );
  }
}

function isEven(number) {
  return number % 2 === 0;
}
