import Piece from '../Piece.js';

export default class King extends Piece {
  constructor(props) {
    super(props, (props === 1 ?
      "https://upload.wikimedia.org/wikipedia/commons/4/42/Chess_klt45.svg" :
      "https://upload.wikimedia.org/wikipedia/commons/f/f0/Chess_kdt45.svg"));
    this.state = {
      firstMove: true,
      captured: false,
      value: 40,
    };
  }

  isValidMove = (origin, destination) => {
    const { firstMove } = this.state;
    const proposedMove = Math.abs(origin - destination);
    let incrementor = 0;
    switch (proposedMove % 9) {
      case 0:
        return incrementor = 9;
      case 8:
        return incrementor = 8;
      case 7:
        return incrementor = 7;
      case 1:
        return incrementor = 1;
      case 2 && firstMove === true:
        return incrementor = 2;
      default:
        alert("The King's movements have been restricted.");
        return incrementor = 400;
    }
  }
}
