import React from 'react';
import ReactDOM from 'react-dom';
import Square from './Square/Square.js';
import './Board.css';

export default class Board extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      initial: true,
    }
  }

  renderSquare = (index, squareShade) => {
    console.log(this.props);
    return(
      <Square
        style={this.props.squares[index] ? this.props.squares[index].style : null}
        shade={squareShade}
        onClick={this.props.selectSquare(index)}
      />
    )
  }

  renderBoard = () => {
    const board = [];
    for(let rankNumber = 0; rankNumber < 8; rankNumber++) {
      const ranks = [];
      for(let file = 0; file < 8; file++) {
        const squareShade = (isEven(rankNumber) && isEven(file)) || (!isEven(rankNumber) && !isEven(file)) ? "light-square" : "dark-square";
        return ranks.push(this.renderSquare((rankNumber * 8) + file, squareShade));
      }
      return board.push(<div className="Board-rank">{ranks}</div>);
    }
    return board;
  }

  render() {
    return(
      <div>
        {this.renderBoard()}
      </div>
    );
  }
}

function isEven(number) {
  return number % 2 === 0;
}
