import React from 'react';
import Pieces from '../Pieces.js'

export default class Pawn extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      firstMove: true,
      captured: false,
    };
  }

  isValidMove = (origin, destination) => {
    const { firstMove } = this.state;
    const proposedMove = Math.abs(origin - destination);
    let incrementor = 0;
    switch (proposedMove) {
      case proposedMove % 9 === 0:
        return incrementor = 9;
        break;
      case proposedMove % 8 === 0:
        return incrementor = 8;
        break;
      case proposedMove % 7 === 0:
        return incrementor = 7;
        break;
      case proposedMove % 1 === 0 && firstMove === true:
        return incrementor = 1;
        break;
      default:
        alert("The" + `${this.name}` + "'s movements have been restricted.");
        return incrementor = 400;
    }
  }

  render() {
    return(
      <div>
      </div>
    )
  }
}
