import React from 'react';
import Piece from '../Piece.js';

export default class Knight extends Piece {
  constructor(props) {
    super(props.player, (props.player === 1 ?
      "https://upload.wikimedia.org/wikipedia/commons/7/70/Chess_nlt45.svg" :
      "https://upload.wikimedia.org/wikipedia/commons/e/ef/Chess_ndt45.svg"));
    this.state = {
      captured: false,
      value: 3,
    };
  }

  isValidMove = (origin, destination) => {
    const proposedMove = Math.abs(origin - destination);
    let incrementor = 0;
    switch (proposedMove) {
      case proposedMove % 17 === 0:
        return incrementor = 17;
        break;
      case proposedMove % 15 === 0:
        return incrementor = 15;
        break;
      case proposedMove % 10 === 0:
        return incrementor = 10;
        break;
      case proposedMove % 6 === 0:
        return incrementor = 6;
        break;
      default:
        alert("The" + `${this.name}` + "'s movements have been restricted.");
        return incrementor = 400;
    }
  }

  render() {
    return(
      <div>
      </div>
    )
  }
}
