import React from 'react';
import './App.css';
import User from './User/User.js';
import UserList from './UserList/UserList.js';
import Game from './Game/Game.js';


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      users: [],
      landingPage: true,
      loginPage: false,
      playGamePage: false,
    }
  }



  async componentDidMount() {
    try{
      const response = await fetch('http://localhost:3000/api/v1/users');
      const users = await response.json();
      this.setState({ users });
    }
    catch(error){
      console.error(error.message);
    }
  }

  renderUser = (user) => {
    return(
      <User
        key={user.id + Date.now()}
        {...user}
      />
    )
  }

  render() {
    const { users, loginPage, playGamePage, landingPage } = this.state;

    if(landingPage) {
      return(
        <div className="App">
          <div className="App-header">
          <img src='https://www.roctechnologies.com/wp-content/uploads/2018/07/chess-2727443_1920.jpg' alt='chess pieces' />
            <div className="App-login">
              THIS IS WHERE USERS LAND!!!
            </div>
          </div>
        </div>
      )
    }

    if(loginPage) {
      return(
        <div className="App">
          <div className="App-header">
            <div className="App-login">
              THIS IS WHERE USERS LOGIN!!!
            </div>
          </div>
        </div>
      )
    }

    if(playGamePage) {
      return (
        <div className="App">
          <div className="App-header">
            <div className="App-login">
              <UserList
                users={users}
                renderUser={this.renderUser}
              />
            </div>
            <div>
              <Game users={users} />
            </div>
            <div className="Padding">
            </div>
          </div>
        </div>
      );
    }
  }
}

export default App;
