import React from 'react';
import Piece from '../Piece.js';

export default class Queen extends Piece {
  constructor(props) {
    super(props.player, (props.player === 1 ?
      "https://upload.wikimedia.org/wikipedia/commons/1/15/Chess_qlt45.svg" :
      "https://upload.wikimedia.org/wikipedia/commons/4/47/Chess_qdt45.svg"));
    this.state = {
      captured: false,
      value: 9,
    };
  }

  isValidMove = (origin, destination) => {
    const proposedMove = Math.abs(origin - destination);
    let incrementor = 0;
    switch (proposedMove) {
      case proposedMove % 9 === 0:
        return incrementor = 9;
        break;
      case proposedMove % 8 === 0:
        return incrementor = 8;
        break;
      case proposedMove % 7 === 0:
        return incrementor = 7;
        break;
      case proposedMove % 1 === 0 && proposedMove < 8:
        return incrementor = 1;
        break;
      default:
        alert("The" + `${this.name}` + "'s movements have been restricted.");
        return incrementor = 400;
    }
  }

  render() {
    return(
      <div>
      </div>
    )
  }
}
