import Piece from '../Piece.js';

export default class Pawn extends Piece {
  constructor(props) {
    super(props, (props === 1 ?
      "https://upload.wikimedia.org/wikipedia/commons/4/45/Chess_plt45.svg" :
      "https://upload.wikimedia.org/wikipedia/commons/c/c7/Chess_pdt45.svg"));
    this.state = {
      firstMove: true,
      captured: false,
      value: 1,
    };
    console.log(this.state);
  }

  isValidMove = (origin, destination) => {
    const { firstMove } = this.state;
    const proposedMove = Math.abs(origin - destination);
    let incrementor = 0;
    switch (proposedMove) {
      case proposedMove % 9 === 0:
        return incrementor = 9;
        break;
      case proposedMove % 8 === 0:
        return incrementor = 8;
        break;
      case proposedMove % 7 === 0:
        return incrementor = 7;
        break;
      case proposedMove % 16 === 0 && firstMove === true:
        return incrementor = 16;
        break;
      default:
        alert("The" + `${this.name}` + "'s movements have been restricted.");
        return incrementor = 400;
    }
  }

  promotion = () => {

  }
}
